mapper.select:
SELECT
        MEM_PWD
  FROM
        MEMBER
 WHERE
        MEM_ID=#{memId}
   AND
        MEM_NICKNAME=#{memNickname}
   AND
        MEM_EMAIL=#{memEmail}

mapper.update:
UPDATE
		MEMBER
   SET
   		MEM_PWD = #{memPwd}
 WHERE
 		MEM_ID= #{memId}

UPDATE MEMBER
SET MEM_PWD = #{memPwd}
WHERE MEM_EMAIL = #{memEmail}

mapper.select:
SELECT *
FROM MEMBER
WHERE MEM_ID = #{memId}
AND MEM_NICKNAME = #{memNickname}
AND MEM_EMAIL = #{memEmail}
